IF EXISTS (SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME='CacheTable') DROP TABLE CacheTable;
CREATE TABLE CacheTable ( Id numeric NOT NULL IDENTITY(1,1) PRIMARY KEY NONCLUSTERED, RequestString nVARCHAR(255), ExpiryDate NUMERIC NOT NULL, SheetUrl nVARCHAR(255), AbsoluteSheetUrl nVARCHAR(255), ContentType nVARCHAR(255), Xml TEXT NOT NULL,Project nVARCHAR(128),[Transaction] nVARCHAR(128));
